mapper.select:
SELECT COUNT(*)
FROM posts
WHERE board_id = #{boardId}

mapper.update:
UPDATE posts
SET
    title = #{title},
    content = #{content},
    emp_id = #{empId}
WHERE id = #{id}

mapper.delete:
DELETE FROM posts
WHERE id IN

    #{id}

mapper.update:
UPDATE posts
SET views = views + 1
WHERE id = #{postId}
